import React, { useState } from "react";
import { Container, Form, Button, Col, Row } from "react-bootstrap";
import validator from "validator";

function SubscribePage() {
  const [isShown, setIsShown] = useState(false);
  const handleMouseEnter = (e) => {
    e.target.style.background = "#FFF";
    e.target.style.color = "#458FF6";
    setIsShown(true);
  };
  const handleMouseLeave = (e) => {
    e.target.style.background = "#458ff6";
    e.target.style.color = "#FFF";
    setIsShown(false);
  };

  const [emailError, setEmailError] = useState("");
  const validateEmail = (e) => {
    var email = e.target.value;

    if (validator.isEmail(email)) {
      setEmailError("Valid Email :)");
    } else {
      setEmailError("Enter valid Email!");
    }
  };

  return (
    <div>
      <Container>
        <h1 className={"featuresPage"}>Subscribe to our Newsletter </h1>
        <hr />
        <Form>
          <Row>
            <Col className={"formGrid"}>
              <Form.Group className={"mb-3 subForm"} controlId="formBasicEmail">
                <Form.Control
                  type="email"
                  placeholder="Enter your email address"
                  onChange={(e) => validateEmail(e)}
                  style={{
                    border: "solid",
                    width: "25rem",
                    color: "#D1D1D1",
                    position: "relative",
                    left: "7rem",
                    padding: "1rem",
                  }}
                />
                {emailError}
              </Form.Group>
            </Col>
            <Col>
              <Button
                onMouseEnter={handleMouseEnter}
                onMouseLeave={handleMouseLeave}
                style={{
                  borderRadius: "20px",
                  height: "40px",
                  width: "170px",
                  fontFamily: "Mulish , sans-serif",
                  fontWeight: "300",
                  fontSize: "0.9rem",
                  backgroundColor: "#458ff6",
                  border: "solid #458ff6",
                  position: "relative",
                  top: "-0.8rem",
                }}
                className={"buttonHeader"}
                size="lg"
              >
                Submit
              </Button>{" "}
              {isShown}
            </Col>
          </Row>
        </Form>
      </Container>
    </div>
  );
}

export default SubscribePage;
