import React, { useState } from "react";

import { Container, Row, Col, Button } from "react-bootstrap";
import { ReactComponent as ManageImg } from "../images/TM-hero-illustration.svg";
function Header() {
  const [isShown, setIsShown] = useState(false);
  return (
    <div>
      <Container>
        <Row>
          <Col xl={6} className={"TaskTitle"}>
            <h1 id="">
              Mange your Task <br /> from anywhere!
            </h1>
            <p className={"grayColor"}>
              {" "}
              Task Manger provide a progressive , cutting-
              <br />
              edg and accessible way to manage your rasks <br />
              from anywhere!
            </p>
            <Button
              onMouseEnter={() => setIsShown(true)}
              onMouseLeave={() => setIsShown(false)}
              style={{
                borderRadius: "20px",
                height: "40px",
                width: "170px",
                fontFamily: "Mulish , sans-serif",
                fontWeight: "300",
                fontSize: "0.9rem",
                backgroundColor: "#458ff6",
                border: "solid #458ff6",
              }}
              className={"buttonHeader"}
              size="lg"
            >
              Try it now!
            </Button>{" "}
            {isShown && (
              <div>
                <Button
                  style={{
                    borderRadius: "20px",
                    height: "40px",
                    width: "170px",
                    fontFamily: "Mulish , sans-serif",
                    color: "#458FF6",
                    fontWeight: "300",
                    fontSize: "0.9rem",
                    backgroundColor: "#FFF",
                    border: "solid #458ff6",
                  }}
                  className={"buttonHeader"}
                  size="lg"
                >
                  Try it now!
                </Button>{" "}
              </div>
            )}{" "}
          </Col>
          <Col xl={4}>
            <ManageImg className={"ImgTitle"} />
          </Col>
        </Row>
      </Container>
    </div>
  );
}
export default Header;
